
public class Uebungen4Woche1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x1 = 1000000;
		System.out.println(x1 * x1);
		//Wertebereicht von int zu klein Vorzeichen wird überschrieben daher minus
		long x2 = 1000000;
		System.out.println(x2 * x2);
		//stimmt da long genug gross ist
		float x3 = 1000000;
		System.out.println(x3 * x3);
		//stimmt auch aber wird als gleitkomma ausgegeben
		
	}

}
